    public class DownloadRepositoryRequest : IRequest<ObjectResponse<FileByteObject>>
    {
        [Required]
        public Guid Id { get; set; }
    }
    internal class DownloadRepositoryHandler : IRequestHandler<DownloadRepositoryRequest, ObjectResponse<FileByteObject>>
    {
        private readonly ILogger _logger;
        private readonly IMediator _mediator;
        private readonly IGeneralHelper _helper;
        private readonly IMemoryCache _cache;
        private readonly IUnitOfWork<ApplicationDBContext> _context;
        public DownloadRepositoryHandler(
            ILogger<DownloadRepositoryHandler> logger,
            IGeneralHelper helper,
            IMemoryCache cache,
            IMediator mediator,
            IUnitOfWork<ApplicationDBContext> context
            )
        {
            _logger = logger;
            _cache = cache;
            _helper = helper;
            _mediator = mediator;
            _context = context;
        }
        public async Task<ObjectResponse<FileByteObject>> Handle(DownloadRepositoryRequest request, CancellationToken cancellationToken)
        {
            ObjectResponse<FileByteObject> result = new ObjectResponse<FileByteObject>();
            try
            {
                string cache_key = $"{CacheKey.REPOSITORY}_{request.Id}";
                Data.Model.Repository item = null;
                if (_cache.TryGetValue(cache_key, out Data.Model.Repository data))
                {
                    item = data;
                }
                else
                {
                    item = await _context.Entity<Tomori.Epartner.Data.Model.Repository>().Where(d => d.Id == request.Id).FirstOrDefaultAsync();
                    if (item == null)
                    {
                        result.NotFound($"Id Repository {request.Id} Tidak Ditemukan");
                        return result;
                    }
                    var cacheEntryOptions = new MemoryCacheEntryOptions()
                            .SetSlidingExpiration(TimeSpan.FromSeconds(60))
                            .SetAbsoluteExpiration(TimeSpan.FromSeconds(3600))
                            .SetPriority(CacheItemPriority.Normal)
                            .SetSize(1024);
                    _cache.Set(cache_key, item, cacheEntryOptions);
                }
                result.Data = new FileByteObject()
                {
                    Filename = item.FileName,
                    MimeType = item.MimeType,
                    FIle = Convert.FromBase64String(item.Base64)
                };
                result.OK();

            }
            catch (Exception ex)
            {
                _logger.LogError(ex, "Failed Download Repository", request.Id);
                result.Error("Failed Download Repository", ex.Message);
            }
            return result;
        }
