$(document).ready(function () {
    $('#Announcement-addBtn').on('click', function () {
        $('.clear').val('');
        $('#md-Announcement-add').modal('show');
        $('#Announcement-create_btn').unbind();
        $('#Announcement-create_btn').on('click', function () {
            addAnnouncementSave();
        });
    });
});

function addAnnouncementSave() {
    ConfirmMessage('Apakah Anda Yakin?', function (isConfirm) {
        if (isConfirm) {
            var param = {
				announcementCategory:$('#Add-Announcement-AnnouncementCategory').val(),
				announcementType:$('#Add-Announcement-AnnouncementType').val(),
				attachment:$('#Add-Announcement-Attachment').val(),
				bidangUsaha:$('#Add-Announcement-BidangUsaha').val(),
				civdId:$('#Add-Announcement-CivdId').val(),
				description:$('#Add-Announcement-Description').val(),
				endDate:$('#Add-Announcement-EndDate').val(),
				golonganUsaha:$('#Add-Announcement-GolonganUsaha').val(),
				k3sId:$('#Add-Announcement-K3sId').val(),
				k3sName:$('#Add-Announcement-K3sName').val(),
				previousId:$('#Add-Announcement-PreviousId').val(),
				publishDate:$('#Add-Announcement-PublishDate').val(),
				tenderType:$('#Add-Announcement-TenderType').val(),
				title:$('#Add-Announcement-Title').val(),

            }
            RequestData('POST', `/v1/Announcement/add`, '#md-Announcement-add', null, JSON.stringify(param), function (data_obj) {
                if (data_obj.succeeded) {
                    ShowNotif("Data Berhasil Disimpan", "success");
                    $('#md-Announcement-add').modal('hide');
                    getListAnnouncement();
                }
                else if (data_obj.code == 401) { //unathorized
                    AlertMessage(data_obj.message);
                } else
                    ShowNotif(`${data_obj.message} : ${data_obj.description}`, "error");
            });
        }
    });
}
