$(document).ready(function () {
    $('#ApiLog-addBtn').on('click', function () {
        $('.clear').val('');
        $('#md-ApiLog-add').modal('show');
        $('#ApiLog-create_btn').unbind();
        $('#ApiLog-create_btn').on('click', function () {
            addApiLogSave();
        });
    });
});

function addApiLogSave() {
    ConfirmMessage('Apakah Anda Yakin?', function (isConfirm) {
        if (isConfirm) {
            var param = {
				endpoint:$('#Add-ApiLog-Endpoint').val(),
				idUser:$('#Add-ApiLog-IdUser').val(),
				request:$('#Add-ApiLog-Request').val(),
				response:$('#Add-ApiLog-Response').val(),

            }
            RequestData('POST', `/v1/ApiLog/add`, '#md-ApiLog-add', null, JSON.stringify(param), function (data_obj) {
                if (data_obj.succeeded) {
                    ShowNotif("Data Berhasil Disimpan", "success");
                    $('#md-ApiLog-add').modal('hide');
                    getListApiLog();
                }
                else if (data_obj.code == 401) { //unathorized
                    AlertMessage(data_obj.message);
                } else
                    ShowNotif(`${data_obj.message} : ${data_obj.description}`, "error");
            });
        }
    });
}
