$(document).ready(function () {
    $('#ChangeConfig-addBtn').on('click', function () {
        $('.clear').val('');
        $('#md-ChangeConfig-add').modal('show');
        $('#ChangeConfig-create_btn').unbind();
        $('#ChangeConfig-create_btn').on('click', function () {
            addChangeConfigSave();
        });
    });
});

function addChangeConfigSave() {
    ConfirmMessage('Apakah Anda Yakin?', function (isConfirm) {
        if (isConfirm) {
            var param = {
				active:$('#Add-ChangeConfig-Active').is(":checked"),
				field:$('#Add-ChangeConfig-Field').val(),
				modul:$('#Add-ChangeConfig-Modul').val(),

            }
            RequestData('POST', `/v1/ChangeConfig/add`, '#md-ChangeConfig-add', null, JSON.stringify(param), function (data_obj) {
                if (data_obj.succeeded) {
                    ShowNotif("Data Berhasil Disimpan", "success");
                    $('#md-ChangeConfig-add').modal('hide');
                    getListChangeConfig();
                }
                else if (data_obj.code == 401) { //unathorized
                    AlertMessage(data_obj.message);
                } else
                    ShowNotif(`${data_obj.message} : ${data_obj.description}`, "error");
            });
        }
    });
}
