function editChangeConfigDialog(el) {
    var data = $(el).data('detail');
    $('.clear').val('');
    $('#md-ChangeConfig-edit').modal('show');

	$('#Edit-ChangeConfig-Active').prop('checked', data.active);
	$('#Edit-ChangeConfig-Field').val(data.field);
	$('#Edit-ChangeConfig-Modul').val(data.modul);


    $('#md-ChangeConfig-edit').data('id', data.id);
    $('#ChangeConfig-edit_btn').unbind();
    $('#ChangeConfig-edit_btn').on('click', function () {
        editChangeConfigSave();
    });
}

function editChangeConfigSave() {
    ConfirmMessage('Apakah Anda Yakin Akan Mengubah Data Ini?', isConfirm => {
        if (isConfirm) {
            var param = {
				active:$('#Edit-ChangeConfig-Active').is(":checked"),
				field:$('#Edit-ChangeConfig-Field').val(),
				modul:$('#Edit-ChangeConfig-Modul').val(),

            }
            RequestData('PUT', `/v1/ChangeConfig/edit/${$('#md-ChangeConfig-edit').data('id')}`, '#md-ChangeConfig-edit .modal-content', null, JSON.stringify(param), function (data_obj) {
                if (data_obj.succeeded) {
                    ShowNotif("Data Berhasil Dirubah", "success");
                    $('#md-ChangeConfig-edit').modal('hide');
                    getListChangeConfig();
                }
                else if (data_obj.code == 401) { //unathorized
                    AlertMessage(data_obj.message);
                } else
                    ShowNotif(`${data_obj.message} : ${data_obj.description}`, "error");
            });
        }
    });
}
