$(document).ready(function () {
    $('#ChangeLog-addBtn').on('click', function () {
        $('.clear').val('');
        $('#md-ChangeLog-add').modal('show');
        $('#ChangeLog-create_btn').unbind();
        $('#ChangeLog-create_btn').on('click', function () {
            addChangeLogSave();
        });
    });
});

function addChangeLogSave() {
    ConfirmMessage('Apakah Anda Yakin?', function (isConfirm) {
        if (isConfirm) {
            var param = {
				idUser:$('#Add-ChangeLog-IdUser').val(),
				primaryKey:$('#Add-ChangeLog-PrimaryKey').val(),
				tableName:$('#Add-ChangeLog-TableName').val(),
				tipe:$('#Add-ChangeLog-Tipe').val(),

            }
            RequestData('POST', `/v1/ChangeLog/add`, '#md-ChangeLog-add', null, JSON.stringify(param), function (data_obj) {
                if (data_obj.succeeded) {
                    ShowNotif("Data Berhasil Disimpan", "success");
                    $('#md-ChangeLog-add').modal('hide');
                    getListChangeLog();
                }
                else if (data_obj.code == 401) { //unathorized
                    AlertMessage(data_obj.message);
                } else
                    ShowNotif(`${data_obj.message} : ${data_obj.description}`, "error");
            });
        }
    });
}
