function editChangeLogDialog(el) {
    var data = $(el).data('detail');
    $('.clear').val('');
    $('#md-ChangeLog-edit').modal('show');

	$('#Edit-ChangeLog-IdUser').val(data.idUser);
	$('#Edit-ChangeLog-PrimaryKey').val(data.primaryKey);
	$('#Edit-ChangeLog-TableName').val(data.tableName);
	$('#Edit-ChangeLog-Tipe').val(data.tipe);


    $('#md-ChangeLog-edit').data('id', data.id);
    $('#ChangeLog-edit_btn').unbind();
    $('#ChangeLog-edit_btn').on('click', function () {
        editChangeLogSave();
    });
}

function editChangeLogSave() {
    ConfirmMessage('Apakah Anda Yakin Akan Mengubah Data Ini?', isConfirm => {
        if (isConfirm) {
            var param = {
				idUser:$('#Edit-ChangeLog-IdUser').val(),
				primaryKey:$('#Edit-ChangeLog-PrimaryKey').val(),
				tableName:$('#Edit-ChangeLog-TableName').val(),
				tipe:$('#Edit-ChangeLog-Tipe').val(),

            }
            RequestData('PUT', `/v1/ChangeLog/edit/${$('#md-ChangeLog-edit').data('id')}`, '#md-ChangeLog-edit .modal-content', null, JSON.stringify(param), function (data_obj) {
                if (data_obj.succeeded) {
                    ShowNotif("Data Berhasil Dirubah", "success");
                    $('#md-ChangeLog-edit').modal('hide');
                    getListChangeLog();
                }
                else if (data_obj.code == 401) { //unathorized
                    AlertMessage(data_obj.message);
                } else
                    ShowNotif(`${data_obj.message} : ${data_obj.description}`, "error");
            });
        }
    });
}
