$(document).ready(function () {
    $('#ChangeLogProperty-addBtn').on('click', function () {
        $('.clear').val('');
        $('#md-ChangeLogProperty-add').modal('show');
        $('#ChangeLogProperty-create_btn').unbind();
        $('#ChangeLogProperty-create_btn').on('click', function () {
            addChangeLogPropertySave();
        });
    });
});

function addChangeLogPropertySave() {
    ConfirmMessage('Apakah Anda Yakin?', function (isConfirm) {
        if (isConfirm) {
            var param = {
				idChangeLog:$('#Add-ChangeLogProperty-IdChangeLog').val(),
				newValue:$('#Add-ChangeLogProperty-NewValue').val(),
				oldValue:$('#Add-ChangeLogProperty-OldValue').val(),
				property:$('#Add-ChangeLogProperty-Property').val(),

            }
            RequestData('POST', `/v1/ChangeLogProperty/add`, '#md-ChangeLogProperty-add', null, JSON.stringify(param), function (data_obj) {
                if (data_obj.succeeded) {
                    ShowNotif("Data Berhasil Disimpan", "success");
                    $('#md-ChangeLogProperty-add').modal('hide');
                    getListChangeLogProperty();
                }
                else if (data_obj.code == 401) { //unathorized
                    AlertMessage(data_obj.message);
                } else
                    ShowNotif(`${data_obj.message} : ${data_obj.description}`, "error");
            });
        }
    });
}
