function editChangeLogPropertyDialog(el) {
    var data = $(el).data('detail');
    $('.clear').val('');
    $('#md-ChangeLogProperty-edit').modal('show');

	$('#Edit-ChangeLogProperty-IdChangeLog').val(data.idChangeLog);
	$('#Edit-ChangeLogProperty-NewValue').val(data.newValue);
	$('#Edit-ChangeLogProperty-OldValue').val(data.oldValue);
	$('#Edit-ChangeLogProperty-Property').val(data.property);


    $('#md-ChangeLogProperty-edit').data('id', data.id);
    $('#ChangeLogProperty-edit_btn').unbind();
    $('#ChangeLogProperty-edit_btn').on('click', function () {
        editChangeLogPropertySave();
    });
}

function editChangeLogPropertySave() {
    ConfirmMessage('Apakah Anda Yakin Akan Mengubah Data Ini?', isConfirm => {
        if (isConfirm) {
            var param = {
				idChangeLog:$('#Edit-ChangeLogProperty-IdChangeLog').val(),
				newValue:$('#Edit-ChangeLogProperty-NewValue').val(),
				oldValue:$('#Edit-ChangeLogProperty-OldValue').val(),
				property:$('#Edit-ChangeLogProperty-Property').val(),

            }
            RequestData('PUT', `/v1/ChangeLogProperty/edit/${$('#md-ChangeLogProperty-edit').data('id')}`, '#md-ChangeLogProperty-edit .modal-content', null, JSON.stringify(param), function (data_obj) {
                if (data_obj.succeeded) {
                    ShowNotif("Data Berhasil Dirubah", "success");
                    $('#md-ChangeLogProperty-edit').modal('hide');
                    getListChangeLogProperty();
                }
                else if (data_obj.code == 401) { //unathorized
                    AlertMessage(data_obj.message);
                } else
                    ShowNotif(`${data_obj.message} : ${data_obj.description}`, "error");
            });
        }
    });
}
