$(document).ready(function () {
    $('#Config-addBtn').on('click', function () {
        $('.clear').val('');
        $('#md-Config-add').modal('show');
        $('#Config-create_btn').unbind();
        $('#Config-create_btn').on('click', function () {
            addConfigSave();
        });
    });
});

function addConfigSave() {
    ConfirmMessage('Apakah Anda Yakin?', function (isConfirm) {
        if (isConfirm) {
            var param = {
				category:$('#Add-Config-Category').val(),
				name:$('#Add-Config-Name').val(),
				value:$('#Add-Config-Value').val(),

            }
            RequestData('POST', `/v1/Config/add`, '#md-Config-add', null, JSON.stringify(param), function (data_obj) {
                if (data_obj.succeeded) {
                    ShowNotif("Data Berhasil Disimpan", "success");
                    $('#md-Config-add').modal('hide');
                    getListConfig();
                }
                else if (data_obj.code == 401) { //unathorized
                    AlertMessage(data_obj.message);
                } else
                    ShowNotif(`${data_obj.message} : ${data_obj.description}`, "error");
            });
        }
    });
}
