function editConfigDialog(el) {
    var data = $(el).data('detail');
    $('.clear').val('');
    $('#md-Config-edit').modal('show');

	$('#Edit-Config-Category').val(data.category);
	$('#Edit-Config-Name').val(data.name);
	$('#Edit-Config-Value').val(data.value);


    $('#md-Config-edit').data('id', data.id);
    $('#Config-edit_btn').unbind();
    $('#Config-edit_btn').on('click', function () {
        editConfigSave();
    });
}

function editConfigSave() {
    ConfirmMessage('Apakah Anda Yakin Akan Mengubah Data Ini?', isConfirm => {
        if (isConfirm) {
            var param = {
				category:$('#Edit-Config-Category').val(),
				name:$('#Edit-Config-Name').val(),
				value:$('#Edit-Config-Value').val(),

            }
            RequestData('PUT', `/v1/Config/edit/${$('#md-Config-edit').data('id')}`, '#md-Config-edit .modal-content', null, JSON.stringify(param), function (data_obj) {
                if (data_obj.succeeded) {
                    ShowNotif("Data Berhasil Dirubah", "success");
                    $('#md-Config-edit').modal('hide');
                    getListConfig();
                }
                else if (data_obj.code == 401) { //unathorized
                    AlertMessage(data_obj.message);
                } else
                    ShowNotif(`${data_obj.message} : ${data_obj.description}`, "error");
            });
        }
    });
}
