$(document).ready(function () {
    $('#CounterTransaction-addBtn').on('click', function () {
        $('.clear').val('');
        $('#md-CounterTransaction-add').modal('show');
        $('#CounterTransaction-create_btn').unbind();
        $('#CounterTransaction-create_btn').on('click', function () {
            addCounterTransactionSave();
        });
    });
});

function addCounterTransactionSave() {
    ConfirmMessage('Apakah Anda Yakin?', function (isConfirm) {
        if (isConfirm) {
            var param = {
				counter:$('#Add-CounterTransaction-Counter').val(),
				modul:$('#Add-CounterTransaction-Modul').val(),
				month:$('#Add-CounterTransaction-Month').val(),
				year:$('#Add-CounterTransaction-Year').val(),

            }
            RequestData('POST', `/v1/CounterTransaction/add`, '#md-CounterTransaction-add', null, JSON.stringify(param), function (data_obj) {
                if (data_obj.succeeded) {
                    ShowNotif("Data Berhasil Disimpan", "success");
                    $('#md-CounterTransaction-add').modal('hide');
                    getListCounterTransaction();
                }
                else if (data_obj.code == 401) { //unathorized
                    AlertMessage(data_obj.message);
                } else
                    ShowNotif(`${data_obj.message} : ${data_obj.description}`, "error");
            });
        }
    });
}
