function editCounterTransactionDialog(el) {
    var data = $(el).data('detail');
    $('.clear').val('');
    $('#md-CounterTransaction-edit').modal('show');

	$('#Edit-CounterTransaction-Counter').val(data.counter);
	$('#Edit-CounterTransaction-Modul').val(data.modul);
	$('#Edit-CounterTransaction-Month').val(data.month);
	$('#Edit-CounterTransaction-Year').val(data.year);


    $('#md-CounterTransaction-edit').data('id', data.id);
    $('#CounterTransaction-edit_btn').unbind();
    $('#CounterTransaction-edit_btn').on('click', function () {
        editCounterTransactionSave();
    });
}

function editCounterTransactionSave() {
    ConfirmMessage('Apakah Anda Yakin Akan Mengubah Data Ini?', isConfirm => {
        if (isConfirm) {
            var param = {
				counter:$('#Edit-CounterTransaction-Counter').val(),
				modul:$('#Edit-CounterTransaction-Modul').val(),
				month:$('#Edit-CounterTransaction-Month').val(),
				year:$('#Edit-CounterTransaction-Year').val(),

            }
            RequestData('PUT', `/v1/CounterTransaction/edit/${$('#md-CounterTransaction-edit').data('id')}`, '#md-CounterTransaction-edit .modal-content', null, JSON.stringify(param), function (data_obj) {
                if (data_obj.succeeded) {
                    ShowNotif("Data Berhasil Dirubah", "success");
                    $('#md-CounterTransaction-edit').modal('hide');
                    getListCounterTransaction();
                }
                else if (data_obj.code == 401) { //unathorized
                    AlertMessage(data_obj.message);
                } else
                    ShowNotif(`${data_obj.message} : ${data_obj.description}`, "error");
            });
        }
    });
}
