$(document).ready(function () {
    $('#DocumentPayment-addBtn').on('click', function () {
        $('.clear').val('');
        $('#md-DocumentPayment-add').modal('show');
        $('#DocumentPayment-create_btn').unbind();
        $('#DocumentPayment-create_btn').on('click', function () {
            addDocumentPaymentSave();
        });
    });
});

function addDocumentPaymentSave() {
    ConfirmMessage('Apakah Anda Yakin?', function (isConfirm) {
        if (isConfirm) {
            var param = {
				active:$('#Add-DocumentPayment-Active').is(":checked"),
				code:$('#Add-DocumentPayment-Code').val(),
				description:$('#Add-DocumentPayment-Description').val(),
				fileExtension:$('#Add-DocumentPayment-FileExtension').val(),
				fileMaxSize:$('#Add-DocumentPayment-FileMaxSize').val(),
				isMandatory:$('#Add-DocumentPayment-IsMandatory').is(":checked"),
				masterValueCode:$('#Add-DocumentPayment-MasterValueCode').val(),
				masterValueText:$('#Add-DocumentPayment-MasterValueText').val(),
				programDana:$('#Add-DocumentPayment-ProgramDana').val(),
				sort:$('#Add-DocumentPayment-Sort').val(),
				title:$('#Add-DocumentPayment-Title').val(),
				type:$('#Add-DocumentPayment-Type').val(),

            }
            RequestData('POST', `/v1/DocumentPayment/add`, '#md-DocumentPayment-add', null, JSON.stringify(param), function (data_obj) {
                if (data_obj.succeeded) {
                    ShowNotif("Data Berhasil Disimpan", "success");
                    $('#md-DocumentPayment-add').modal('hide');
                    getListDocumentPayment();
                }
                else if (data_obj.code == 401) { //unathorized
                    AlertMessage(data_obj.message);
                } else
                    ShowNotif(`${data_obj.message} : ${data_obj.description}`, "error");
            });
        }
    });
}
