function editDocumentPaymentDialog(el) {
    var data = $(el).data('detail');
    $('.clear').val('');
    $('#md-DocumentPayment-edit').modal('show');

	$('#Edit-DocumentPayment-Active').prop('checked', data.active);
	$('#Edit-DocumentPayment-Code').val(data.code);
	$('#Edit-DocumentPayment-Description').val(data.description);
	$('#Edit-DocumentPayment-FileExtension').val(data.fileExtension);
	$('#Edit-DocumentPayment-FileMaxSize').val(data.fileMaxSize);
	$('#Edit-DocumentPayment-IsMandatory').prop('checked', data.isMandatory);
	$('#Edit-DocumentPayment-MasterValueCode').val(data.masterValueCode);
	$('#Edit-DocumentPayment-MasterValueText').val(data.masterValueText);
	$('#Edit-DocumentPayment-ProgramDana').val(data.programDana);
	$('#Edit-DocumentPayment-Sort').val(data.sort);
	$('#Edit-DocumentPayment-Title').val(data.title);
	$('#Edit-DocumentPayment-Type').val(data.type);


    $('#md-DocumentPayment-edit').data('id', data.id);
    $('#DocumentPayment-edit_btn').unbind();
    $('#DocumentPayment-edit_btn').on('click', function () {
        editDocumentPaymentSave();
    });
}

function editDocumentPaymentSave() {
    ConfirmMessage('Apakah Anda Yakin Akan Mengubah Data Ini?', isConfirm => {
        if (isConfirm) {
            var param = {
				active:$('#Edit-DocumentPayment-Active').is(":checked"),
				code:$('#Edit-DocumentPayment-Code').val(),
				description:$('#Edit-DocumentPayment-Description').val(),
				fileExtension:$('#Edit-DocumentPayment-FileExtension').val(),
				fileMaxSize:$('#Edit-DocumentPayment-FileMaxSize').val(),
				isMandatory:$('#Edit-DocumentPayment-IsMandatory').is(":checked"),
				masterValueCode:$('#Edit-DocumentPayment-MasterValueCode').val(),
				masterValueText:$('#Edit-DocumentPayment-MasterValueText').val(),
				programDana:$('#Edit-DocumentPayment-ProgramDana').val(),
				sort:$('#Edit-DocumentPayment-Sort').val(),
				title:$('#Edit-DocumentPayment-Title').val(),
				type:$('#Edit-DocumentPayment-Type').val(),

            }
            RequestData('PUT', `/v1/DocumentPayment/edit/${$('#md-DocumentPayment-edit').data('id')}`, '#md-DocumentPayment-edit .modal-content', null, JSON.stringify(param), function (data_obj) {
                if (data_obj.succeeded) {
                    ShowNotif("Data Berhasil Dirubah", "success");
                    $('#md-DocumentPayment-edit').modal('hide');
                    getListDocumentPayment();
                }
                else if (data_obj.code == 401) { //unathorized
                    AlertMessage(data_obj.message);
                } else
                    ShowNotif(`${data_obj.message} : ${data_obj.description}`, "error");
            });
        }
    });
}
