$(document).ready(function () {
    getListDocumentPayment();
});

function getListDocumentPayment(page) {
    page = page != undefined ? page : 1;
    var pageSize = $('#DocumentPayment-page_select').val();
    var element = {
        table: '#DocumentPayment-table',
        tbody: '#DocumentPayment-tbody',
        from: '#DocumentPayment-from_page',
        to: '#DocumentPayment-to_page',
        total: '#DocumentPayment-total',
        pagination: '#DocumentPayment-pagination',
        item_pagination: 'DocumentPayment-item'
    }
    var param = {
        sort: {
            field: "id",
            type: 0
        },
        start: page,
        length: pageSize
    }
    RequestData('POST', "/v1/DocumentPayment/list", element.table, element.tbody, JSON.stringify(param), function (data) {
        if (data.succeeded) {
            $(element.tbody).html('');
            $(element.pagination).html('');
            if (data.list.length > 0) {
                SetTableData(true, 12, element, {
                    page: page,
                    pageSize: pageSize,
                    count: data.count,
                    function_name: 'getListDocumentPayment'
                }, function (count) {
                    data.list.forEach(function (item) {
                        $(element.tbody).append(`
                            <tr>
                                <td class="text-nowrap text-center">${count}</td>
								<td class="text-nowrap">${item.active}</td>
								<td class="text-nowrap">${item.code}</td>
								<td class="text-nowrap">${item.description}</td>
								<td class="text-nowrap">${item.fileExtension}</td>
								<td class="text-nowrap">${item.fileMaxSize}</td>
								<td class="text-nowrap">${item.isMandatory}</td>
								<td class="text-nowrap">${item.masterValueCode}</td>
								<td class="text-nowrap">${item.masterValueText}</td>
								<td class="text-nowrap">${item.programDana}</td>
								<td class="text-nowrap">${item.sort}</td>
								<td class="text-nowrap">${item.title}</td>
								<td class="text-nowrap">${item.type}</td>

                                <td class="text-center">
                                    <div>
                                        <button type="button" data-toggle="dropdown" class="btn btn-sm btn-info dropdown-toggle" aria-hashpopup="true" aria-expanded="false">Aksi</button>
                                        <div class="dropdown-menu">
                                            <a href="#!" class="dropdown-item" onclick="detailDocumentPaymentDialog(this);" data-detail='${JSON.stringify(item).replace(/' /g, " " ) }'>Detail</a>
                                            <a href="#!" class="dropdown-item" onclick="editDocumentPaymentDialog(this);" data-detail='${JSON.stringify(item).replace(/' /g, " " ) }'>Edit</a>
                                            <a href="#!" class="dropdown-item" onclick="deleteDocumentPaymentDialog(${item.id})">Delete</a>
                                        </div>
                                    </div>
                                </td>
                            </tr>
                        `);
                        count++;
                    });
                });
            } else {
                settabledata(false, 12, element);
            }
        } else {
            ShowNotif(`${data.message} : ${data.description}`, "error");
        }
    })
}

