$(document).ready(function () {
    $('#DocumentTemplate-addBtn').on('click', function () {
        $('.clear').val('');
        $('#md-DocumentTemplate-add').modal('show');
        $('#DocumentTemplate-create_btn').unbind();
        $('#DocumentTemplate-create_btn').on('click', function () {
            addDocumentTemplateSave();
        });
    });
});

function addDocumentTemplateSave() {
    ConfirmMessage('Apakah Anda Yakin?', function (isConfirm) {
        if (isConfirm) {
            var param = {
				active:$('#Add-DocumentTemplate-Active').is(":checked"),
				code:$('#Add-DocumentTemplate-Code').val(),
				subject:$('#Add-DocumentTemplate-Subject').val(),
				value:$('#Add-DocumentTemplate-Value').val(),

            }
            RequestData('POST', `/v1/DocumentTemplate/add`, '#md-DocumentTemplate-add', null, JSON.stringify(param), function (data_obj) {
                if (data_obj.succeeded) {
                    ShowNotif("Data Berhasil Disimpan", "success");
                    $('#md-DocumentTemplate-add').modal('hide');
                    getListDocumentTemplate();
                }
                else if (data_obj.code == 401) { //unathorized
                    AlertMessage(data_obj.message);
                } else
                    ShowNotif(`${data_obj.message} : ${data_obj.description}`, "error");
            });
        }
    });
}
