function editDocumentTemplateDialog(el) {
    var data = $(el).data('detail');
    $('.clear').val('');
    $('#md-DocumentTemplate-edit').modal('show');

	$('#Edit-DocumentTemplate-Active').prop('checked', data.active);
	$('#Edit-DocumentTemplate-Code').val(data.code);
	$('#Edit-DocumentTemplate-Subject').val(data.subject);
	$('#Edit-DocumentTemplate-Value').val(data.value);


    $('#md-DocumentTemplate-edit').data('id', data.id);
    $('#DocumentTemplate-edit_btn').unbind();
    $('#DocumentTemplate-edit_btn').on('click', function () {
        editDocumentTemplateSave();
    });
}

function editDocumentTemplateSave() {
    ConfirmMessage('Apakah Anda Yakin Akan Mengubah Data Ini?', isConfirm => {
        if (isConfirm) {
            var param = {
				active:$('#Edit-DocumentTemplate-Active').is(":checked"),
				code:$('#Edit-DocumentTemplate-Code').val(),
				subject:$('#Edit-DocumentTemplate-Subject').val(),
				value:$('#Edit-DocumentTemplate-Value').val(),

            }
            RequestData('PUT', `/v1/DocumentTemplate/edit/${$('#md-DocumentTemplate-edit').data('id')}`, '#md-DocumentTemplate-edit .modal-content', null, JSON.stringify(param), function (data_obj) {
                if (data_obj.succeeded) {
                    ShowNotif("Data Berhasil Dirubah", "success");
                    $('#md-DocumentTemplate-edit').modal('hide');
                    getListDocumentTemplate();
                }
                else if (data_obj.code == 401) { //unathorized
                    AlertMessage(data_obj.message);
                } else
                    ShowNotif(`${data_obj.message} : ${data_obj.description}`, "error");
            });
        }
    });
}
