$(document).ready(function () {
    $('#Faq-addBtn').on('click', function () {
        $('.clear').val('');
        $('#md-Faq-add').modal('show');
        $('#Faq-create_btn').unbind();
        $('#Faq-create_btn').on('click', function () {
            addFaqSave();
        });
    });
});

function addFaqSave() {
    ConfirmMessage('Apakah Anda Yakin?', function (isConfirm) {
        if (isConfirm) {
            var param = {
				active:$('#Add-Faq-Active').is(":checked"),
				answer:$('#Add-Faq-Answer').val(),
				questionnaire:$('#Add-Faq-Questionnaire').val(),
				section:$('#Add-Faq-Section').val(),

            }
            RequestData('POST', `/v1/Faq/add`, '#md-Faq-add', null, JSON.stringify(param), function (data_obj) {
                if (data_obj.succeeded) {
                    ShowNotif("Data Berhasil Disimpan", "success");
                    $('#md-Faq-add').modal('hide');
                    getListFaq();
                }
                else if (data_obj.code == 401) { //unathorized
                    AlertMessage(data_obj.message);
                } else
                    ShowNotif(`${data_obj.message} : ${data_obj.description}`, "error");
            });
        }
    });
}
