function editFaqDialog(el) {
    var data = $(el).data('detail');
    $('.clear').val('');
    $('#md-Faq-edit').modal('show');

	$('#Edit-Faq-Active').prop('checked', data.active);
	$('#Edit-Faq-Answer').val(data.answer);
	$('#Edit-Faq-Questionnaire').val(data.questionnaire);
	$('#Edit-Faq-Section').val(data.section);


    $('#md-Faq-edit').data('id', data.id);
    $('#Faq-edit_btn').unbind();
    $('#Faq-edit_btn').on('click', function () {
        editFaqSave();
    });
}

function editFaqSave() {
    ConfirmMessage('Apakah Anda Yakin Akan Mengubah Data Ini?', isConfirm => {
        if (isConfirm) {
            var param = {
				active:$('#Edit-Faq-Active').is(":checked"),
				answer:$('#Edit-Faq-Answer').val(),
				questionnaire:$('#Edit-Faq-Questionnaire').val(),
				section:$('#Edit-Faq-Section').val(),

            }
            RequestData('PUT', `/v1/Faq/edit/${$('#md-Faq-edit').data('id')}`, '#md-Faq-edit .modal-content', null, JSON.stringify(param), function (data_obj) {
                if (data_obj.succeeded) {
                    ShowNotif("Data Berhasil Dirubah", "success");
                    $('#md-Faq-edit').modal('hide');
                    getListFaq();
                }
                else if (data_obj.code == 401) { //unathorized
                    AlertMessage(data_obj.message);
                } else
                    ShowNotif(`${data_obj.message} : ${data_obj.description}`, "error");
            });
        }
    });
}
