function editFaqQuestionnaireDialog(el) {
    var data = $(el).data('detail');
    $('.clear').val('');
    $('#md-FaqQuestionnaire-edit').modal('show');

	$('#Edit-FaqQuestionnaire-Answer').val(data.answer);
	$('#Edit-FaqQuestionnaire-Questionnaire').val(data.questionnaire);
	$('#Edit-FaqQuestionnaire-Section').val(data.section);


    $('#md-FaqQuestionnaire-edit').data('id', data.id);
    $('#FaqQuestionnaire-edit_btn').unbind();
    $('#FaqQuestionnaire-edit_btn').on('click', function () {
        editFaqQuestionnaireSave();
    });
}

function editFaqQuestionnaireSave() {
    ConfirmMessage('Apakah Anda Yakin Akan Mengubah Data Ini?', isConfirm => {
        if (isConfirm) {
            var param = {
				answer:$('#Edit-FaqQuestionnaire-Answer').val(),
				questionnaire:$('#Edit-FaqQuestionnaire-Questionnaire').val(),
				section:$('#Edit-FaqQuestionnaire-Section').val(),

            }
            RequestData('PUT', `/v1/FaqQuestionnaire/edit/${$('#md-FaqQuestionnaire-edit').data('id')}`, '#md-FaqQuestionnaire-edit .modal-content', null, JSON.stringify(param), function (data_obj) {
                if (data_obj.succeeded) {
                    ShowNotif("Data Berhasil Dirubah", "success");
                    $('#md-FaqQuestionnaire-edit').modal('hide');
                    getListFaqQuestionnaire();
                }
                else if (data_obj.code == 401) { //unathorized
                    AlertMessage(data_obj.message);
                } else
                    ShowNotif(`${data_obj.message} : ${data_obj.description}`, "error");
            });
        }
    });
}
