$(document).ready(function () {
    $('#HisSanksi-addBtn').on('click', function () {
        $('.clear').val('');
        $('#md-HisSanksi-add').modal('show');
        $('#HisSanksi-create_btn').unbind();
        $('#HisSanksi-create_btn').on('click', function () {
            addHisSanksiSave();
        });
    });
});

function addHisSanksiSave() {
    ConfirmMessage('Apakah Anda Yakin?', function (isConfirm) {
        if (isConfirm) {
            var param = {
				filePernyataanPerbaikan:$('#Add-HisSanksi-FilePernyataanPerbaikan').val(),
				fileSuratSanksi:$('#Add-HisSanksi-FileSuratSanksi').val(),
				fileSuratSanksiId:$('#Add-HisSanksi-FileSuratSanksiId').val(),
				keterangan:$('#Add-HisSanksi-Keterangan').val(),
				percobaan:$('#Add-HisSanksi-Percobaan').val(),
				sanksi:$('#Add-HisSanksi-Sanksi').val(),
				tglBerakhirPercobaan:$('#Add-HisSanksi-TglBerakhirPercobaan').val(),
				tglBerakhirSanksi:$('#Add-HisSanksi-TglBerakhirSanksi').val(),
				tglBerlakuSanksi:$('#Add-HisSanksi-TglBerlakuSanksi').val(),
				tglPelepasanSanksi:$('#Add-HisSanksi-TglPelepasanSanksi').val(),
				vendorId:$('#Add-HisSanksi-VendorId').val(),

            }
            RequestData('POST', `/v1/HisSanksi/add`, '#md-HisSanksi-add', null, JSON.stringify(param), function (data_obj) {
                if (data_obj.succeeded) {
                    ShowNotif("Data Berhasil Disimpan", "success");
                    $('#md-HisSanksi-add').modal('hide');
                    getListHisSanksi();
                }
                else if (data_obj.code == 401) { //unathorized
                    AlertMessage(data_obj.message);
                } else
                    ShowNotif(`${data_obj.message} : ${data_obj.description}`, "error");
            });
        }
    });
}
