function editHisSanksiDialog(el) {
    var data = $(el).data('detail');
    $('.clear').val('');
    $('#md-HisSanksi-edit').modal('show');

	$('#Edit-HisSanksi-FilePernyataanPerbaikan').val(data.filePernyataanPerbaikan);
	$('#Edit-HisSanksi-FilePernyataanPerbaikanId').val(data.filePernyataanPerbaikanId);
	$('#Edit-HisSanksi-FileSuratSanksi').val(data.fileSuratSanksi);
	$('#Edit-HisSanksi-FileSuratSanksiId').val(data.fileSuratSanksiId);
	$('#Edit-HisSanksi-Keterangan').val(data.keterangan);
	$('#Edit-HisSanksi-Percobaan').val(data.percobaan);
	$('#Edit-HisSanksi-Sanksi').val(data.sanksi);
	$('#Edit-HisSanksi-TglBerakhirPercobaan').val(data.tglBerakhirPercobaan);
	$('#Edit-HisSanksi-TglBerakhirSanksi').val(data.tglBerakhirSanksi);
	$('#Edit-HisSanksi-TglBerlakuSanksi').val(data.tglBerlakuSanksi);
	$('#Edit-HisSanksi-TglPelepasanSanksi').val(data.tglPelepasanSanksi);
	$('#Edit-HisSanksi-VendorId').val(data.vendorId);


    $('#md-HisSanksi-edit').data('id', data.id);
    $('#HisSanksi-edit_btn').unbind();
    $('#HisSanksi-edit_btn').on('click', function () {
        editHisSanksiSave();
    });
}

function editHisSanksiSave() {
    ConfirmMessage('Apakah Anda Yakin Akan Mengubah Data Ini?', isConfirm => {
        if (isConfirm) {
            var param = {
				filePernyataanPerbaikan:$('#Edit-HisSanksi-FilePernyataanPerbaikan').val(),
				filePernyataanPerbaikanId:$('#Edit-HisSanksi-FilePernyataanPerbaikanId').val(),
				fileSuratSanksi:$('#Edit-HisSanksi-FileSuratSanksi').val(),
				fileSuratSanksiId:$('#Edit-HisSanksi-FileSuratSanksiId').val(),
				keterangan:$('#Edit-HisSanksi-Keterangan').val(),
				percobaan:$('#Edit-HisSanksi-Percobaan').val(),
				sanksi:$('#Edit-HisSanksi-Sanksi').val(),
				tglBerakhirPercobaan:$('#Edit-HisSanksi-TglBerakhirPercobaan').val(),
				tglBerakhirSanksi:$('#Edit-HisSanksi-TglBerakhirSanksi').val(),
				tglBerlakuSanksi:$('#Edit-HisSanksi-TglBerlakuSanksi').val(),
				tglPelepasanSanksi:$('#Edit-HisSanksi-TglPelepasanSanksi').val(),
				vendorId:$('#Edit-HisSanksi-VendorId').val(),

            }
            RequestData('PUT', `/v1/HisSanksi/edit/${$('#md-HisSanksi-edit').data('id')}`, '#md-HisSanksi-edit .modal-content', null, JSON.stringify(param), function (data_obj) {
                if (data_obj.succeeded) {
                    ShowNotif("Data Berhasil Dirubah", "success");
                    $('#md-HisSanksi-edit').modal('hide');
                    getListHisSanksi();
                }
                else if (data_obj.code == 401) { //unathorized
                    AlertMessage(data_obj.message);
                } else
                    ShowNotif(`${data_obj.message} : ${data_obj.description}`, "error");
            });
        }
    });
}
