$(document).ready(function () {
    getListHisSanksi();
});

function getListHisSanksi(page) {
    page = page != undefined ? page : 1;
    var pageSize = $('#HisSanksi-page_select').val();
    var element = {
        table: '#HisSanksi-table',
        tbody: '#HisSanksi-tbody',
        from: '#HisSanksi-from_page',
        to: '#HisSanksi-to_page',
        total: '#HisSanksi-total',
        pagination: '#HisSanksi-pagination',
        item_pagination: 'HisSanksi-item'
    }
    var param = {
        sort: {
            field: "id",
            type: 0
        },
        start: page,
        length: pageSize
    }
    RequestData('POST', "/v1/HisSanksi/list", element.table, element.tbody, JSON.stringify(param), function (data) {
        if (data.succeeded) {
            $(element.tbody).html('');
            $(element.pagination).html('');
            if (data.list.length > 0) {
                SetTableData(true, 12, element, {
                    page: page,
                    pageSize: pageSize,
                    count: data.count,
                    function_name: 'getListHisSanksi'
                }, function (count) {
                    data.list.forEach(function (item) {
                        $(element.tbody).append(`
                            <tr>
                                <td class="text-nowrap text-center">${count}</td>
								<td class="text-nowrap">${item.filePernyataanPerbaikan}</td>
								<td class="text-nowrap">${item.fileSuratSanksi}</td>
								<td class="text-nowrap">${item.fileSuratSanksiId}</td>
								<td class="text-nowrap">${item.keterangan}</td>
								<td class="text-nowrap">${item.percobaan}</td>
								<td class="text-nowrap">${item.sanksi}</td>
								<td class="text-nowrap">${item.tglBerakhirPercobaan}</td>
								<td class="text-nowrap">${item.tglBerakhirSanksi}</td>
								<td class="text-nowrap">${item.tglBerlakuSanksi}</td>
								<td class="text-nowrap">${item.tglPelepasanSanksi}</td>
								<td class="text-nowrap">${item.vendorId}</td>

                                <td class="text-center">
                                    <div>
                                        <button type="button" data-toggle="dropdown" class="btn btn-sm btn-info dropdown-toggle" aria-hashpopup="true" aria-expanded="false">Aksi</button>
                                        <div class="dropdown-menu">
                                            <a href="#!" class="dropdown-item" onclick="detailHisSanksiDialog(this);" data-detail='${JSON.stringify(item).replace(/' /g, " " ) }'>Detail</a>
                                            <a href="#!" class="dropdown-item" onclick="editHisSanksiDialog(this);" data-detail='${JSON.stringify(item).replace(/' /g, " " ) }'>Edit</a>
                                            <a href="#!" class="dropdown-item" onclick="deleteHisSanksiDialog(${item.id})">Delete</a>
                                        </div>
                                    </div>
                                </td>
                            </tr>
                        `);
                        count++;
                    });
                });
            } else {
                settabledata(false, 12, element);
            }
        } else {
            ShowNotif(`${data.message} : ${data.description}`, "error");
        }
    })
}

