$(document).ready(function () {
    $('#HisSpda-addBtn').on('click', function () {
        $('.clear').val('');
        $('#md-HisSpda-add').modal('show');
        $('#HisSpda-create_btn').unbind();
        $('#HisSpda-create_btn').on('click', function () {
            addHisSpdaSave();
        });
    });
});

function addHisSpdaSave() {
    ConfirmMessage('Apakah Anda Yakin?', function (isConfirm) {
        if (isConfirm) {
            var param = {
				expiredDate:$('#Add-HisSpda-ExpiredDate').val(),
				fileSpda:$('#Add-HisSpda-FileSpda').val(),
				fileSpdaId:$('#Add-HisSpda-FileSpdaId').val(),
				spdaNo:$('#Add-HisSpda-SpdaNo').val(),
				spdaValidity:$('#Add-HisSpda-SpdaValidity').val(),
				uploadDate:$('#Add-HisSpda-UploadDate').val(),
				vendorId:$('#Add-HisSpda-VendorId').val(),

            }
            RequestData('POST', `/v1/HisSpda/add`, '#md-HisSpda-add', null, JSON.stringify(param), function (data_obj) {
                if (data_obj.succeeded) {
                    ShowNotif("Data Berhasil Disimpan", "success");
                    $('#md-HisSpda-add').modal('hide');
                    getListHisSpda();
                }
                else if (data_obj.code == 401) { //unathorized
                    AlertMessage(data_obj.message);
                } else
                    ShowNotif(`${data_obj.message} : ${data_obj.description}`, "error");
            });
        }
    });
}
