function editHisSpdaDialog(el) {
    var data = $(el).data('detail');
    $('.clear').val('');
    $('#md-HisSpda-edit').modal('show');

	$('#Edit-HisSpda-ExpiredDate').val(data.expiredDate);
	$('#Edit-HisSpda-FileSpda').val(data.fileSpda);
	$('#Edit-HisSpda-FileSpdaId').val(data.fileSpdaId);
	$('#Edit-HisSpda-SpdaNo').val(data.spdaNo);
	$('#Edit-HisSpda-SpdaValidity').val(data.spdaValidity);
	$('#Edit-HisSpda-UploadDate').val(data.uploadDate);
	$('#Edit-HisSpda-VendorId').val(data.vendorId);


    $('#md-HisSpda-edit').data('id', data.id);
    $('#HisSpda-edit_btn').unbind();
    $('#HisSpda-edit_btn').on('click', function () {
        editHisSpdaSave();
    });
}

function editHisSpdaSave() {
    ConfirmMessage('Apakah Anda Yakin Akan Mengubah Data Ini?', isConfirm => {
        if (isConfirm) {
            var param = {
				expiredDate:$('#Edit-HisSpda-ExpiredDate').val(),
				fileSpda:$('#Edit-HisSpda-FileSpda').val(),
				fileSpdaId:$('#Edit-HisSpda-FileSpdaId').val(),
				spdaNo:$('#Edit-HisSpda-SpdaNo').val(),
				spdaValidity:$('#Edit-HisSpda-SpdaValidity').val(),
				uploadDate:$('#Edit-HisSpda-UploadDate').val(),
				vendorId:$('#Edit-HisSpda-VendorId').val(),

            }
            RequestData('PUT', `/v1/HisSpda/edit/${$('#md-HisSpda-edit').data('id')}`, '#md-HisSpda-edit .modal-content', null, JSON.stringify(param), function (data_obj) {
                if (data_obj.succeeded) {
                    ShowNotif("Data Berhasil Dirubah", "success");
                    $('#md-HisSpda-edit').modal('hide');
                    getListHisSpda();
                }
                else if (data_obj.code == 401) { //unathorized
                    AlertMessage(data_obj.message);
                } else
                    ShowNotif(`${data_obj.message} : ${data_obj.description}`, "error");
            });
        }
    });
}
