$(document).ready(function () {
    $('#MailLog-addBtn').on('click', function () {
        $('.clear').val('');
        $('#md-MailLog-add').modal('show');
        $('#MailLog-create_btn').unbind();
        $('#MailLog-create_btn').on('click', function () {
            addMailLogSave();
        });
    });
});

function addMailLogSave() {
    ConfirmMessage('Apakah Anda Yakin?', function (isConfirm) {
        if (isConfirm) {
            var param = {
				bodyMail:$('#Add-MailLog-BodyMail').val(),
				ccMail:$('#Add-MailLog-CcMail').val(),
				idUser:$('#Add-MailLog-IdUser').val(),
				senderMail:$('#Add-MailLog-SenderMail').val(),
				subject:$('#Add-MailLog-Subject').val(),
				toMail:$('#Add-MailLog-ToMail').val(),

            }
            RequestData('POST', `/v1/MailLog/add`, '#md-MailLog-add', null, JSON.stringify(param), function (data_obj) {
                if (data_obj.succeeded) {
                    ShowNotif("Data Berhasil Disimpan", "success");
                    $('#md-MailLog-add').modal('hide');
                    getListMailLog();
                }
                else if (data_obj.code == 401) { //unathorized
                    AlertMessage(data_obj.message);
                } else
                    ShowNotif(`${data_obj.message} : ${data_obj.description}`, "error");
            });
        }
    });
}
