function editMailLogDialog(el) {
    var data = $(el).data('detail');
    $('.clear').val('');
    $('#md-MailLog-edit').modal('show');

	$('#Edit-MailLog-BodyMail').val(data.bodyMail);
	$('#Edit-MailLog-CcMail').val(data.ccMail);
	$('#Edit-MailLog-IdUser').val(data.idUser);
	$('#Edit-MailLog-SenderMail').val(data.senderMail);
	$('#Edit-MailLog-Subject').val(data.subject);
	$('#Edit-MailLog-ToMail').val(data.toMail);


    $('#md-MailLog-edit').data('id', data.id);
    $('#MailLog-edit_btn').unbind();
    $('#MailLog-edit_btn').on('click', function () {
        editMailLogSave();
    });
}

function editMailLogSave() {
    ConfirmMessage('Apakah Anda Yakin Akan Mengubah Data Ini?', isConfirm => {
        if (isConfirm) {
            var param = {
				bodyMail:$('#Edit-MailLog-BodyMail').val(),
				ccMail:$('#Edit-MailLog-CcMail').val(),
				idUser:$('#Edit-MailLog-IdUser').val(),
				senderMail:$('#Edit-MailLog-SenderMail').val(),
				subject:$('#Edit-MailLog-Subject').val(),
				toMail:$('#Edit-MailLog-ToMail').val(),

            }
            RequestData('PUT', `/v1/MailLog/edit/${$('#md-MailLog-edit').data('id')}`, '#md-MailLog-edit .modal-content', null, JSON.stringify(param), function (data_obj) {
                if (data_obj.succeeded) {
                    ShowNotif("Data Berhasil Dirubah", "success");
                    $('#md-MailLog-edit').modal('hide');
                    getListMailLog();
                }
                else if (data_obj.code == 401) { //unathorized
                    AlertMessage(data_obj.message);
                } else
                    ShowNotif(`${data_obj.message} : ${data_obj.description}`, "error");
            });
        }
    });
}
