$(document).ready(function () {
    getListLandasanHukum();
});

function getListLandasanHukum(page) {
    page = page != undefined ? page : 1;
    var pageSize = $('#LandasanHukum-page_select').val();
    var element = {
        table: '#LandasanHukum-table',
        tbody: '#LandasanHukum-tbody',
        from: '#LandasanHukum-from_page',
        to: '#LandasanHukum-to_page',
        total: '#LandasanHukum-total',
        pagination: '#LandasanHukum-pagination',
        item_pagination: 'LandasanHukum-item'
    }
    var param = {
        sort: {
            field: "id",
            type: 0
        },
        start: page,
        length: pageSize
    }
    RequestData('POST', "/v1/LandasanHukum/list", element.table, element.tbody, JSON.stringify(param), function (data) {
        if (data.succeeded) {
            $(element.tbody).html('');
            $(element.pagination).html('');
            if (data.list.length > 0) {
                SetTableData(true, 12, element, {
                    page: page,
                    pageSize: pageSize,
                    count: data.count,
                    function_name: 'getListLandasanHukum'
                }, function (count) {
                    data.list.forEach(function (item) {
                        $(element.tbody).append(`
                            <tr>
                                <td class="text-nowrap text-center">${count}</td>
								<td class="text-nowrap">${item.fileLandasanHukum}</td>
								<td class="text-nowrap">${item.fileLandasanHukumId}</td>
								<td class="text-nowrap">${item.jenisAkta}</td>
								<td class="text-nowrap">${item.namaNotaris}</td>
								<td class="text-nowrap">${item.namaSkMenteri}</td>
								<td class="text-nowrap">${item.noAkta}</td>
								<td class="text-nowrap">${item.tglAkta}</td>
								<td class="text-nowrap">${item.vendorId}</td>

                                <td class="text-center">
                                    <div>
                                        <button type="button" data-toggle="dropdown" class="btn btn-sm btn-info dropdown-toggle" aria-hashpopup="true" aria-expanded="false">Aksi</button>
                                        <div class="dropdown-menu">
                                            <a href="#!" class="dropdown-item" onclick="detailLandasanHukumDialog(this);" data-detail='${JSON.stringify(item).replace(/' /g, " " ) }'>Detail</a>
                                            <a href="#!" class="dropdown-item" onclick="editLandasanHukumDialog(this);" data-detail='${JSON.stringify(item).replace(/' /g, " " ) }'>Edit</a>
                                            <a href="#!" class="dropdown-item" onclick="deleteLandasanHukumDialog(${item.id})">Delete</a>
                                        </div>
                                    </div>
                                </td>
                            </tr>
                        `);
                        count++;
                    });
                });
            } else {
                settabledata(false, 12, element);
            }
        } else {
            ShowNotif(`${data.message} : ${data.description}`, "error");
        }
    })
}

