$(document).ready(function () {
    $('#Notification-addBtn').on('click', function () {
        $('.clear').val('');
        $('#md-Notification-add').modal('show');
        $('#Notification-create_btn').unbind();
        $('#Notification-create_btn').on('click', function () {
            addNotificationSave();
        });
    });
});

function addNotificationSave() {
    ConfirmMessage('Apakah Anda Yakin?', function (isConfirm) {
        if (isConfirm) {
            var param = {
				description:$('#Add-Notification-Description').val(),
				idUser:$('#Add-Notification-IdUser').val(),
				isOpen:$('#Add-Notification-IsOpen').is(":checked"),
				navigation:$('#Add-Notification-Navigation').val(),
				subject:$('#Add-Notification-Subject').val(),

            }
            RequestData('POST', `/v1/Notification/add`, '#md-Notification-add', null, JSON.stringify(param), function (data_obj) {
                if (data_obj.succeeded) {
                    ShowNotif("Data Berhasil Disimpan", "success");
                    $('#md-Notification-add').modal('hide');
                    getListNotification();
                }
                else if (data_obj.code == 401) { //unathorized
                    AlertMessage(data_obj.message);
                } else
                    ShowNotif(`${data_obj.message} : ${data_obj.description}`, "error");
            });
        }
    });
}
