function editNotificationDialog(el) {
    var data = $(el).data('detail');
    $('.clear').val('');
    $('#md-Notification-edit').modal('show');

	$('#Edit-Notification-Description').val(data.description);
	$('#Edit-Notification-IdUser').val(data.idUser);
	$('#Edit-Notification-IsOpen').prop('checked', data.isOpen);
	$('#Edit-Notification-Navigation').val(data.navigation);
	$('#Edit-Notification-Subject').val(data.subject);


    $('#md-Notification-edit').data('id', data.id);
    $('#Notification-edit_btn').unbind();
    $('#Notification-edit_btn').on('click', function () {
        editNotificationSave();
    });
}

function editNotificationSave() {
    ConfirmMessage('Apakah Anda Yakin Akan Mengubah Data Ini?', isConfirm => {
        if (isConfirm) {
            var param = {
				description:$('#Edit-Notification-Description').val(),
				idUser:$('#Edit-Notification-IdUser').val(),
				isOpen:$('#Edit-Notification-IsOpen').is(":checked"),
				navigation:$('#Edit-Notification-Navigation').val(),
				subject:$('#Edit-Notification-Subject').val(),

            }
            RequestData('PUT', `/v1/Notification/edit/${$('#md-Notification-edit').data('id')}`, '#md-Notification-edit .modal-content', null, JSON.stringify(param), function (data_obj) {
                if (data_obj.succeeded) {
                    ShowNotif("Data Berhasil Dirubah", "success");
                    $('#md-Notification-edit').modal('hide');
                    getListNotification();
                }
                else if (data_obj.code == 401) { //unathorized
                    AlertMessage(data_obj.message);
                } else
                    ShowNotif(`${data_obj.message} : ${data_obj.description}`, "error");
            });
        }
    });
}
