$(document).ready(function () {
    $('#Page-addBtn').on('click', function () {
        $('.clear').val('');
        $('#md-Page-add').modal('show');
        $('#Page-create_btn').unbind();
        $('#Page-create_btn').on('click', function () {
            addPageSave();
        });
    });
});

function addPageSave() {
    ConfirmMessage('Apakah Anda Yakin?', function (isConfirm) {
        if (isConfirm) {
            var param = {
				active:$('#Add-Page-Active').is(":checked"),
				code:$('#Add-Page-Code').val(),
				description:$('#Add-Page-Description').val(),
				icon:$('#Add-Page-Icon').val(),
				idParent:$('#Add-Page-IdParent').val(),
				name:$('#Add-Page-Name').val(),
				navigation:$('#Add-Page-Navigation').val(),
				section:$('#Add-Page-Section').val(),
				sort:$('#Add-Page-Sort').val(),

            }
            RequestData('POST', `/v1/Page/add`, '#md-Page-add', null, JSON.stringify(param), function (data_obj) {
                if (data_obj.succeeded) {
                    ShowNotif("Data Berhasil Disimpan", "success");
                    $('#md-Page-add').modal('hide');
                    getListPage();
                }
                else if (data_obj.code == 401) { //unathorized
                    AlertMessage(data_obj.message);
                } else
                    ShowNotif(`${data_obj.message} : ${data_obj.description}`, "error");
            });
        }
    });
}
