function editPageDialog(el) {
    var data = $(el).data('detail');
    $('.clear').val('');
    $('#md-Page-edit').modal('show');

	$('#Edit-Page-Active').prop('checked', data.active);
	$('#Edit-Page-Code').val(data.code);
	$('#Edit-Page-Description').val(data.description);
	$('#Edit-Page-Icon').val(data.icon);
	$('#Edit-Page-IdParent').val(data.idParent);
	$('#Edit-Page-Name').val(data.name);
	$('#Edit-Page-Navigation').val(data.navigation);
	$('#Edit-Page-Section').val(data.section);
	$('#Edit-Page-Sort').val(data.sort);


    $('#md-Page-edit').data('id', data.id);
    $('#Page-edit_btn').unbind();
    $('#Page-edit_btn').on('click', function () {
        editPageSave();
    });
}

function editPageSave() {
    ConfirmMessage('Apakah Anda Yakin Akan Mengubah Data Ini?', isConfirm => {
        if (isConfirm) {
            var param = {
				active:$('#Edit-Page-Active').is(":checked"),
				code:$('#Edit-Page-Code').val(),
				description:$('#Edit-Page-Description').val(),
				icon:$('#Edit-Page-Icon').val(),
				idParent:$('#Edit-Page-IdParent').val(),
				name:$('#Edit-Page-Name').val(),
				navigation:$('#Edit-Page-Navigation').val(),
				section:$('#Edit-Page-Section').val(),
				sort:$('#Edit-Page-Sort').val(),

            }
            RequestData('PUT', `/v1/Page/edit/${$('#md-Page-edit').data('id')}`, '#md-Page-edit .modal-content', null, JSON.stringify(param), function (data_obj) {
                if (data_obj.succeeded) {
                    ShowNotif("Data Berhasil Dirubah", "success");
                    $('#md-Page-edit').modal('hide');
                    getListPage();
                }
                else if (data_obj.code == 401) { //unathorized
                    AlertMessage(data_obj.message);
                } else
                    ShowNotif(`${data_obj.message} : ${data_obj.description}`, "error");
            });
        }
    });
}
