$(document).ready(function () {
    getListPage();
});

function getListPage(page) {
    page = page != undefined ? page : 1;
    var pageSize = $('#Page-page_select').val();
    var element = {
        table: '#Page-table',
        tbody: '#Page-tbody',
        from: '#Page-from_page',
        to: '#Page-to_page',
        total: '#Page-total',
        pagination: '#Page-pagination',
        item_pagination: 'Page-item'
    }
    var param = {
        sort: {
            field: "id",
            type: 0
        },
        start: page,
        length: pageSize
    }
    RequestData('POST', "/v1/Page/list", element.table, element.tbody, JSON.stringify(param), function (data) {
        if (data.succeeded) {
            $(element.tbody).html('');
            $(element.pagination).html('');
            if (data.list.length > 0) {
                SetTableData(true, 12, element, {
                    page: page,
                    pageSize: pageSize,
                    count: data.count,
                    function_name: 'getListPage'
                }, function (count) {
                    data.list.forEach(function (item) {
                        $(element.tbody).append(`
                            <tr>
                                <td class="text-nowrap text-center">${count}</td>
								<td class="text-nowrap">${item.active}</td>
								<td class="text-nowrap">${item.code}</td>
								<td class="text-nowrap">${item.description}</td>
								<td class="text-nowrap">${item.icon}</td>
								<td class="text-nowrap">${item.idParent}</td>
								<td class="text-nowrap">${item.name}</td>
								<td class="text-nowrap">${item.navigation}</td>
								<td class="text-nowrap">${item.section}</td>
								<td class="text-nowrap">${item.sort}</td>

                                <td class="text-center">
                                    <div>
                                        <button type="button" data-toggle="dropdown" class="btn btn-sm btn-info dropdown-toggle" aria-hashpopup="true" aria-expanded="false">Aksi</button>
                                        <div class="dropdown-menu">
                                            <a href="#!" class="dropdown-item" onclick="detailPageDialog(this);" data-detail='${JSON.stringify(item).replace(/' /g, " " ) }'>Detail</a>
                                            <a href="#!" class="dropdown-item" onclick="editPageDialog(this);" data-detail='${JSON.stringify(item).replace(/' /g, " " ) }'>Edit</a>
                                            <a href="#!" class="dropdown-item" onclick="deletePageDialog(${item.id})">Delete</a>
                                        </div>
                                    </div>
                                </td>
                            </tr>
                        `);
                        count++;
                    });
                });
            } else {
                settabledata(false, 12, element);
            }
        } else {
            ShowNotif(`${data.message} : ${data.description}`, "error");
        }
    })
}

