function editReportDialog(el) {
    var data = $(el).data('detail');
    $('.clear').val('');
    $('#md-Report-edit').modal('show');

	$('#Edit-Report-Active').prop('checked', data.active);
	$('#Edit-Report-Description').val(data.description);
	$('#Edit-Report-Modul').val(data.modul);
	$('#Edit-Report-Name').val(data.name);
	$('#Edit-Report-Query').val(data.query);


    $('#md-Report-edit').data('id', data.id);
    $('#Report-edit_btn').unbind();
    $('#Report-edit_btn').on('click', function () {
        editReportSave();
    });
}

function editReportSave() {
    ConfirmMessage('Apakah Anda Yakin Akan Mengubah Data Ini?', isConfirm => {
        if (isConfirm) {
            var param = {
				active:$('#Edit-Report-Active').is(":checked"),
				description:$('#Edit-Report-Description').val(),
				modul:$('#Edit-Report-Modul').val(),
				name:$('#Edit-Report-Name').val(),
				query:$('#Edit-Report-Query').val(),

            }
            RequestData('PUT', `/v1/Report/edit/${$('#md-Report-edit').data('id')}`, '#md-Report-edit .modal-content', null, JSON.stringify(param), function (data_obj) {
                if (data_obj.succeeded) {
                    ShowNotif("Data Berhasil Dirubah", "success");
                    $('#md-Report-edit').modal('hide');
                    getListReport();
                }
                else if (data_obj.code == 401) { //unathorized
                    AlertMessage(data_obj.message);
                } else
                    ShowNotif(`${data_obj.message} : ${data_obj.description}`, "error");
            });
        }
    });
}
