$(document).ready(function () {
    $('#ReportRole-addBtn').on('click', function () {
        $('.clear').val('');
        $('#md-ReportRole-add').modal('show');
        $('#ReportRole-create_btn').unbind();
        $('#ReportRole-create_btn').on('click', function () {
            addReportRoleSave();
        });
    });
});

function addReportRoleSave() {
    ConfirmMessage('Apakah Anda Yakin?', function (isConfirm) {
        if (isConfirm) {
            var param = {
				idReport:$('#Add-ReportRole-IdReport').val(),
				idRole:$('#Add-ReportRole-IdRole').val(),

            }
            RequestData('POST', `/v1/ReportRole/add`, '#md-ReportRole-add', null, JSON.stringify(param), function (data_obj) {
                if (data_obj.succeeded) {
                    ShowNotif("Data Berhasil Disimpan", "success");
                    $('#md-ReportRole-add').modal('hide');
                    getListReportRole();
                }
                else if (data_obj.code == 401) { //unathorized
                    AlertMessage(data_obj.message);
                } else
                    ShowNotif(`${data_obj.message} : ${data_obj.description}`, "error");
            });
        }
    });
}
