function editReportRoleDialog(el) {
    var data = $(el).data('detail');
    $('.clear').val('');
    $('#md-ReportRole-edit').modal('show');

	$('#Edit-ReportRole-IdReport').val(data.idReport);
	$('#Edit-ReportRole-IdRole').val(data.idRole);


    $('#md-ReportRole-edit').data('id', data.id);
    $('#ReportRole-edit_btn').unbind();
    $('#ReportRole-edit_btn').on('click', function () {
        editReportRoleSave();
    });
}

function editReportRoleSave() {
    ConfirmMessage('Apakah Anda Yakin Akan Mengubah Data Ini?', isConfirm => {
        if (isConfirm) {
            var param = {
				idReport:$('#Edit-ReportRole-IdReport').val(),
				idRole:$('#Edit-ReportRole-IdRole').val(),

            }
            RequestData('PUT', `/v1/ReportRole/edit/${$('#md-ReportRole-edit').data('id')}`, '#md-ReportRole-edit .modal-content', null, JSON.stringify(param), function (data_obj) {
                if (data_obj.succeeded) {
                    ShowNotif("Data Berhasil Dirubah", "success");
                    $('#md-ReportRole-edit').modal('hide');
                    getListReportRole();
                }
                else if (data_obj.code == 401) { //unathorized
                    AlertMessage(data_obj.message);
                } else
                    ShowNotif(`${data_obj.message} : ${data_obj.description}`, "error");
            });
        }
    });
}
