$(document).ready(function () {
    $('#Repository-addBtn').on('click', function () {
        $('.clear').val('');
        $('#md-Repository-add').modal('show');
        $('#Repository-create_btn').unbind();
        $('#Repository-create_btn').on('click', function () {
            addRepositorySave();
        });
    });
});

function addRepositorySave() {
    ConfirmMessage('Apakah Anda Yakin?', function (isConfirm) {
        if (isConfirm) {
            var param = {
				base64:$('#Add-Repository-Base64').val(),
				code:$('#Add-Repository-Code').val(),
				description:$('#Add-Repository-Description').val(),
				extension:$('#Add-Repository-Extension').val(),
				fileName:$('#Add-Repository-FileName').val(),
				mimeType:$('#Add-Repository-MimeType').val(),
				modul:$('#Add-Repository-Modul').val(),

            }
            RequestData('POST', `/v1/Repository/add`, '#md-Repository-add', null, JSON.stringify(param), function (data_obj) {
                if (data_obj.succeeded) {
                    ShowNotif("Data Berhasil Disimpan", "success");
                    $('#md-Repository-add').modal('hide');
                    getListRepository();
                }
                else if (data_obj.code == 401) { //unathorized
                    AlertMessage(data_obj.message);
                } else
                    ShowNotif(`${data_obj.message} : ${data_obj.description}`, "error");
            });
        }
    });
}
