function editRoleDialog(el) {
    var data = $(el).data('detail');
    $('.clear').val('');
    $('#md-Role-edit').modal('show');

	$('#Edit-Role-Active').prop('checked', data.active);
	$('#Edit-Role-Name').val(data.name);


    $('#md-Role-edit').data('id', data.id);
    $('#Role-edit_btn').unbind();
    $('#Role-edit_btn').on('click', function () {
        editRoleSave();
    });
}

function editRoleSave() {
    ConfirmMessage('Apakah Anda Yakin Akan Mengubah Data Ini?', isConfirm => {
        if (isConfirm) {
            var param = {
				active:$('#Edit-Role-Active').is(":checked"),
				name:$('#Edit-Role-Name').val(),

            }
            RequestData('PUT', `/v1/Role/edit/${$('#md-Role-edit').data('id')}`, '#md-Role-edit .modal-content', null, JSON.stringify(param), function (data_obj) {
                if (data_obj.succeeded) {
                    ShowNotif("Data Berhasil Dirubah", "success");
                    $('#md-Role-edit').modal('hide');
                    getListRole();
                }
                else if (data_obj.code == 401) { //unathorized
                    AlertMessage(data_obj.message);
                } else
                    ShowNotif(`${data_obj.message} : ${data_obj.description}`, "error");
            });
        }
    });
}
