$(document).ready(function () {
    $('#RolePermission-addBtn').on('click', function () {
        $('.clear').val('');
        $('#md-RolePermission-add').modal('show');
        $('#RolePermission-create_btn').unbind();
        $('#RolePermission-create_btn').on('click', function () {
            addRolePermissionSave();
        });
    });
});

function addRolePermissionSave() {
    ConfirmMessage('Apakah Anda Yakin?', function (isConfirm) {
        if (isConfirm) {
            var param = {
				idPermission:$('#Add-RolePermission-IdPermission').val(),
				idRole:$('#Add-RolePermission-IdRole').val(),

            }
            RequestData('POST', `/v1/RolePermission/add`, '#md-RolePermission-add', null, JSON.stringify(param), function (data_obj) {
                if (data_obj.succeeded) {
                    ShowNotif("Data Berhasil Disimpan", "success");
                    $('#md-RolePermission-add').modal('hide');
                    getListRolePermission();
                }
                else if (data_obj.code == 401) { //unathorized
                    AlertMessage(data_obj.message);
                } else
                    ShowNotif(`${data_obj.message} : ${data_obj.description}`, "error");
            });
        }
    });
}
