function editRolePermissionDialog(el) {
    var data = $(el).data('detail');
    $('.clear').val('');
    $('#md-RolePermission-edit').modal('show');

	$('#Edit-RolePermission-IdPermission').val(data.idPermission);
	$('#Edit-RolePermission-IdRole').val(data.idRole);


    $('#md-RolePermission-edit').data('id', data.id);
    $('#RolePermission-edit_btn').unbind();
    $('#RolePermission-edit_btn').on('click', function () {
        editRolePermissionSave();
    });
}

function editRolePermissionSave() {
    ConfirmMessage('Apakah Anda Yakin Akan Mengubah Data Ini?', isConfirm => {
        if (isConfirm) {
            var param = {
				idPermission:$('#Edit-RolePermission-IdPermission').val(),
				idRole:$('#Edit-RolePermission-IdRole').val(),

            }
            RequestData('PUT', `/v1/RolePermission/edit/${$('#md-RolePermission-edit').data('id')}`, '#md-RolePermission-edit .modal-content', null, JSON.stringify(param), function (data_obj) {
                if (data_obj.succeeded) {
                    ShowNotif("Data Berhasil Dirubah", "success");
                    $('#md-RolePermission-edit').modal('hide');
                    getListRolePermission();
                }
                else if (data_obj.code == 401) { //unathorized
                    AlertMessage(data_obj.message);
                } else
                    ShowNotif(`${data_obj.message} : ${data_obj.description}`, "error");
            });
        }
    });
}
