$(document).ready(function () {
    $('#Afiliasi-addBtn').on('click', function () {
        $('.clear').val('');
        $('#md-Afiliasi-add').modal('show');
        $('#Afiliasi-create_btn').unbind();
        $('#Afiliasi-create_btn').on('click', function () {
            addAfiliasiSave();
        });
    });
});

function addAfiliasiSave() {
    ConfirmMessage('Apakah Anda Yakin?', function (isConfirm) {
        if (isConfirm) {
            var param = {
				deskripsi:$('#Add-Afiliasi-Deskripsi').val(),
				fileAfiliasiId:$('#Add-Afiliasi-FileAfiliasiId').val(),
				share:$('#Add-Afiliasi-Share').val(),
				terafiliasi:$('#Add-Afiliasi-Terafiliasi').val(),
				tipeAfiliasi:$('#Add-Afiliasi-TipeAfiliasi').val(),
				vendorId:$('#Add-Afiliasi-VendorId').val(),

            }
            RequestData('POST', `/v1/Afiliasi/add`, '#md-Afiliasi-add', null, JSON.stringify(param), function (data_obj) {
                if (data_obj.succeeded) {
                    ShowNotif("Data Berhasil Disimpan", "success");
                    $('#md-Afiliasi-add').modal('hide');
                    getListAfiliasi();
                }
                else if (data_obj.code == 401) { //unathorized
                    AlertMessage(data_obj.message);
                } else
                    ShowNotif(`${data_obj.message} : ${data_obj.description}`, "error");
            });
        }
    });
}
