function editAfiliasiDialog(el) {
    var data = $(el).data('detail');
    $('.clear').val('');
    $('#md-Afiliasi-edit').modal('show');

	$('#Edit-Afiliasi-Deskripsi').val(data.deskripsi);
	$('#Edit-Afiliasi-FileAfiliasiId').val(data.fileAfiliasiId);
	$('#Edit-Afiliasi-Share').val(data.share);
	$('#Edit-Afiliasi-Terafiliasi').val(data.terafiliasi);
	$('#Edit-Afiliasi-TipeAfiliasi').val(data.tipeAfiliasi);
	$('#Edit-Afiliasi-VendorId').val(data.vendorId);


    $('#md-Afiliasi-edit').data('id', data.id);
    $('#Afiliasi-edit_btn').unbind();
    $('#Afiliasi-edit_btn').on('click', function () {
        editAfiliasiSave();
    });
}

function editAfiliasiSave() {
    ConfirmMessage('Apakah Anda Yakin Akan Mengubah Data Ini?', isConfirm => {
        if (isConfirm) {
            var param = {
				deskripsi:$('#Edit-Afiliasi-Deskripsi').val(),
				fileAfiliasiId:$('#Edit-Afiliasi-FileAfiliasiId').val(),
				share:$('#Edit-Afiliasi-Share').val(),
				terafiliasi:$('#Edit-Afiliasi-Terafiliasi').val(),
				tipeAfiliasi:$('#Edit-Afiliasi-TipeAfiliasi').val(),
				vendorId:$('#Edit-Afiliasi-VendorId').val(),

            }
            RequestData('PUT', `/v1/Afiliasi/edit/${$('#md-Afiliasi-edit').data('id')}`, '#md-Afiliasi-edit .modal-content', null, JSON.stringify(param), function (data_obj) {
                if (data_obj.succeeded) {
                    ShowNotif("Data Berhasil Dirubah", "success");
                    $('#md-Afiliasi-edit').modal('hide');
                    getListAfiliasi();
                }
                else if (data_obj.code == 401) { //unathorized
                    AlertMessage(data_obj.message);
                } else
                    ShowNotif(`${data_obj.message} : ${data_obj.description}`, "error");
            });
        }
    });
}
