function editAnnouncementDialog(el) {
    var data = $(el).data('detail');
    $('.clear').val('');
    $('#md-Announcement-edit').modal('show');

	$('#Edit-Announcement-AnnouncementCategory').val(data.announcementCategory);
	$('#Edit-Announcement-AnnouncementType').val(data.announcementType);
	$('#Edit-Announcement-Attachment').val(data.attachment);
	$('#Edit-Announcement-BidangUsaha').val(data.bidangUsaha);
	$('#Edit-Announcement-Description').val(data.description);
	$('#Edit-Announcement-EndDate').val(data.endDate);
	$('#Edit-Announcement-GolonganUsaha').val(data.golonganUsaha);
	$('#Edit-Announcement-K3sId').val(data.k3sId);
	$('#Edit-Announcement-K3sName').val(data.k3sName);
	$('#Edit-Announcement-PreviousId').val(data.previousId);
	$('#Edit-Announcement-PublishDate').val(data.publishDate);
	$('#Edit-Announcement-TenderType').val(data.tenderType);
	$('#Edit-Announcement-Title').val(data.title);


    $('#md-Announcement-edit').data('id', data.id);
    $('#Announcement-edit_btn').unbind();
    $('#Announcement-edit_btn').on('click', function () {
        editAnnouncementSave();
    });
}

function editAnnouncementSave() {
    ConfirmMessage('Apakah Anda Yakin Akan Mengubah Data Ini?', isConfirm => {
        if (isConfirm) {
            var param = {
				announcementCategory:$('#Edit-Announcement-AnnouncementCategory').val(),
				announcementType:$('#Edit-Announcement-AnnouncementType').val(),
				attachment:$('#Edit-Announcement-Attachment').val(),
				bidangUsaha:$('#Edit-Announcement-BidangUsaha').val(),
				description:$('#Edit-Announcement-Description').val(),
				endDate:$('#Edit-Announcement-EndDate').val(),
				golonganUsaha:$('#Edit-Announcement-GolonganUsaha').val(),
				k3sId:$('#Edit-Announcement-K3sId').val(),
				k3sName:$('#Edit-Announcement-K3sName').val(),
				previousId:$('#Edit-Announcement-PreviousId').val(),
				publishDate:$('#Edit-Announcement-PublishDate').val(),
				tenderType:$('#Edit-Announcement-TenderType').val(),
				title:$('#Edit-Announcement-Title').val(),

            }
            RequestData('PUT', `/v1/Announcement/edit/${$('#md-Announcement-edit').data('id')}`, '#md-Announcement-edit .modal-content', null, JSON.stringify(param), function (data_obj) {
                if (data_obj.succeeded) {
                    ShowNotif("Data Berhasil Dirubah", "success");
                    $('#md-Announcement-edit').modal('hide');
                    getListAnnouncement();
                }
                else if (data_obj.code == 401) { //unathorized
                    AlertMessage(data_obj.message);
                } else
                    ShowNotif(`${data_obj.message} : ${data_obj.description}`, "error");
            });
        }
    });
}
