$(document).ready(function () {
    $('#IzinUsaha-addBtn').on('click', function () {
        $('.clear').val('');
        $('#md-IzinUsaha-add').modal('show');
        $('#IzinUsaha-create_btn').unbind();
        $('#IzinUsaha-create_btn').on('click', function () {
            addIzinUsahaSave();
        });
    });
});

function addIzinUsahaSave() {
    ConfirmMessage('Apakah Anda Yakin?', function (isConfirm) {
        if (isConfirm) {
            var param = {
				akhirBerlaku:$('#Add-IzinUsaha-AkhirBerlaku').val(),
				bidangUsaha:$('#Add-IzinUsaha-BidangUsaha').val(),
				fileIzinUsaha:$('#Add-IzinUsaha-FileIzinUsaha').val(),
				fileIzinUsahaId:$('#Add-IzinUsaha-FileIzinUsahaId').val(),
				golonganUsaha:$('#Add-IzinUsaha-GolonganUsaha').val(),
				instansiPemberiIzin:$('#Add-IzinUsaha-InstansiPemberiIzin').val(),
				jenisIzinUsaha:$('#Add-IzinUsaha-JenisIzinUsaha').val(),
				jenisMataUang:$('#Add-IzinUsaha-JenisMataUang').val(),
				kekayaanBershi:$('#Add-IzinUsaha-KekayaanBershi').val(),
				merkStp:$('#Add-IzinUsaha-MerkStp').val(),
				mulaiBerlaku:$('#Add-IzinUsaha-MulaiBerlaku').val(),
				noIzinUsaha:$('#Add-IzinUsaha-NoIzinUsaha').val(),
				other:$('#Add-IzinUsaha-Other').val(),
				peringkatInspeksi:$('#Add-IzinUsaha-PeringkatInspeksi').val(),
				tipeStp:$('#Add-IzinUsaha-TipeStp').val(),
				vendorId:$('#Add-IzinUsaha-VendorId').val(),

            }
            RequestData('POST', `/v1/IzinUsaha/add`, '#md-IzinUsaha-add', null, JSON.stringify(param), function (data_obj) {
                if (data_obj.succeeded) {
                    ShowNotif("Data Berhasil Disimpan", "success");
                    $('#md-IzinUsaha-add').modal('hide');
                    getListIzinUsaha();
                }
                else if (data_obj.code == 401) { //unathorized
                    AlertMessage(data_obj.message);
                } else
                    ShowNotif(`${data_obj.message} : ${data_obj.description}`, "error");
            });
        }
    });
}
