$(document).ready(function () {
    $('#Kompetensi-addBtn').on('click', function () {
        $('.clear').val('');
        $('#md-Kompetensi-add').modal('show');
        $('#Kompetensi-create_btn').unbind();
        $('#Kompetensi-create_btn').on('click', function () {
            addKompetensiSave();
        });
    });
});

function addKompetensiSave() {
    ConfirmMessage('Apakah Anda Yakin?', function (isConfirm) {
        if (isConfirm) {
            var param = {
				bidangSubBidang:$('#Add-Kompetensi-BidangSubBidang').val(),
				bidangSubBidangCode:$('#Add-Kompetensi-BidangSubBidangCode').val(),
				deskripsi:$('#Add-Kompetensi-Deskripsi').val(),
				document:$('#Add-Kompetensi-Document').val(),
				documentId:$('#Add-Kompetensi-DocumentId').val(),
				jenisMataUang:$('#Add-Kompetensi-JenisMataUang').val(),
				nilaiKontrakPoso:$('#Add-Kompetensi-NilaiKontrakPoso').val(),
				noKontrakPoso:$('#Add-Kompetensi-NoKontrakPoso').val(),
				perusahaan:$('#Add-Kompetensi-Perusahaan').val(),
				progressKontrakPoso:$('#Add-Kompetensi-ProgressKontrakPoso').val(),
				tglKontrakPoso:$('#Add-Kompetensi-TglKontrakPoso').val(),
				tglPenyelesaian:$('#Add-Kompetensi-TglPenyelesaian').val(),
				vendorId:$('#Add-Kompetensi-VendorId').val(),

            }
            RequestData('POST', `/v1/Kompetensi/add`, '#md-Kompetensi-add', null, JSON.stringify(param), function (data_obj) {
                if (data_obj.succeeded) {
                    ShowNotif("Data Berhasil Disimpan", "success");
                    $('#md-Kompetensi-add').modal('hide');
                    getListKompetensi();
                }
                else if (data_obj.code == 401) { //unathorized
                    AlertMessage(data_obj.message);
                } else
                    ShowNotif(`${data_obj.message} : ${data_obj.description}`, "error");
            });
        }
    });
}
