function editKompetensiDialog(el) {
    var data = $(el).data('detail');
    $('.clear').val('');
    $('#md-Kompetensi-edit').modal('show');

	$('#Edit-Kompetensi-BidangSubBidang').val(data.bidangSubBidang);
	$('#Edit-Kompetensi-BidangSubBidangCode').val(data.bidangSubBidangCode);
	$('#Edit-Kompetensi-Deskripsi').val(data.deskripsi);
	$('#Edit-Kompetensi-Document').val(data.document);
	$('#Edit-Kompetensi-DocumentId').val(data.documentId);
	$('#Edit-Kompetensi-JenisMataUang').val(data.jenisMataUang);
	$('#Edit-Kompetensi-NilaiKontrakPoso').val(data.nilaiKontrakPoso);
	$('#Edit-Kompetensi-NoKontrakPoso').val(data.noKontrakPoso);
	$('#Edit-Kompetensi-Perusahaan').val(data.perusahaan);
	$('#Edit-Kompetensi-ProgressKontrakPoso').val(data.progressKontrakPoso);
	$('#Edit-Kompetensi-TglKontrakPoso').val(data.tglKontrakPoso);
	$('#Edit-Kompetensi-TglPenyelesaian').val(data.tglPenyelesaian);
	$('#Edit-Kompetensi-VendorId').val(data.vendorId);


    $('#md-Kompetensi-edit').data('id', data.id);
    $('#Kompetensi-edit_btn').unbind();
    $('#Kompetensi-edit_btn').on('click', function () {
        editKompetensiSave();
    });
}

function editKompetensiSave() {
    ConfirmMessage('Apakah Anda Yakin Akan Mengubah Data Ini?', isConfirm => {
        if (isConfirm) {
            var param = {
				bidangSubBidang:$('#Edit-Kompetensi-BidangSubBidang').val(),
				bidangSubBidangCode:$('#Edit-Kompetensi-BidangSubBidangCode').val(),
				deskripsi:$('#Edit-Kompetensi-Deskripsi').val(),
				document:$('#Edit-Kompetensi-Document').val(),
				documentId:$('#Edit-Kompetensi-DocumentId').val(),
				jenisMataUang:$('#Edit-Kompetensi-JenisMataUang').val(),
				nilaiKontrakPoso:$('#Edit-Kompetensi-NilaiKontrakPoso').val(),
				noKontrakPoso:$('#Edit-Kompetensi-NoKontrakPoso').val(),
				perusahaan:$('#Edit-Kompetensi-Perusahaan').val(),
				progressKontrakPoso:$('#Edit-Kompetensi-ProgressKontrakPoso').val(),
				tglKontrakPoso:$('#Edit-Kompetensi-TglKontrakPoso').val(),
				tglPenyelesaian:$('#Edit-Kompetensi-TglPenyelesaian').val(),
				vendorId:$('#Edit-Kompetensi-VendorId').val(),

            }
            RequestData('PUT', `/v1/Kompetensi/edit/${$('#md-Kompetensi-edit').data('id')}`, '#md-Kompetensi-edit .modal-content', null, JSON.stringify(param), function (data_obj) {
                if (data_obj.succeeded) {
                    ShowNotif("Data Berhasil Dirubah", "success");
                    $('#md-Kompetensi-edit').modal('hide');
                    getListKompetensi();
                }
                else if (data_obj.code == 401) { //unathorized
                    AlertMessage(data_obj.message);
                } else
                    ShowNotif(`${data_obj.message} : ${data_obj.description}`, "error");
            });
        }
    });
}
