$(document).ready(function () {
    $('#Neraca-addBtn').on('click', function () {
        $('.clear').val('');
        $('#md-Neraca-add').modal('show');
        $('#Neraca-create_btn').unbind();
        $('#Neraca-create_btn').on('click', function () {
            addNeracaSave();
        });
    });
});

function addNeracaSave() {
    ConfirmMessage('Apakah Anda Yakin?', function (isConfirm) {
        if (isConfirm) {
            var param = {
				accountReceivables:$('#Add-Neraca-AccountReceivables').val(),
				akhirBerlaku:$('#Add-Neraca-AkhirBerlaku').val(),
				cash:$('#Add-Neraca-Cash').val(),
				costOfRevenue:$('#Add-Neraca-CostOfRevenue').val(),
				currentAsset:$('#Add-Neraca-CurrentAsset').val(),
				currentLiabilities:$('#Add-Neraca-CurrentLiabilities').val(),
				depreciationExpense:$('#Add-Neraca-DepreciationExpense').val(),
				earningBeforeTax:$('#Add-Neraca-EarningBeforeTax').val(),
				ebit:$('#Add-Neraca-Ebit').val(),
				fileNeraca:$('#Add-Neraca-FileNeraca').val(),
				fileNeracaId:$('#Add-Neraca-FileNeracaId').val(),
				fixedAsset:$('#Add-Neraca-FixedAsset').val(),
				golonganPerusahaan:$('#Add-Neraca-GolonganPerusahaan').val(),
				grossProfit:$('#Add-Neraca-GrossProfit').val(),
				interestExpense:$('#Add-Neraca-InterestExpense').val(),
				jenisMataUang:$('#Add-Neraca-JenisMataUang').val(),
				jenisMataUangSales:$('#Add-Neraca-JenisMataUangSales').val(),
				jumlahAktiva:$('#Add-Neraca-JumlahAktiva').val(),
				jumlahHutang:$('#Add-Neraca-JumlahHutang').val(),
				kekayaanBersih:$('#Add-Neraca-KekayaanBersih').val(),
				netEkuitas:$('#Add-Neraca-NetEkuitas').val(),
				netProfit:$('#Add-Neraca-NetProfit').val(),
				nonCurrentLiabilities:$('#Add-Neraca-NonCurrentLiabilities').val(),
				operatingExpense:$('#Add-Neraca-OperatingExpense').val(),
				otherCurrentAsset:$('#Add-Neraca-OtherCurrentAsset').val(),
				othersExpense:$('#Add-Neraca-OthersExpense').val(),
				othersIncome:$('#Add-Neraca-OthersIncome').val(),
				penjualan:$('#Add-Neraca-Penjualan').val(),
				periodeAkhir:$('#Add-Neraca-PeriodeAkhir').val(),
				periodeAwal:$('#Add-Neraca-PeriodeAwal').val(),
				statusAudit:$('#Add-Neraca-StatusAudit').val(),
				tahun:$('#Add-Neraca-Tahun').val(),
				tanahBangunan:$('#Add-Neraca-TanahBangunan').val(),
				tglAkhirSales:$('#Add-Neraca-TglAkhirSales').val(),
				tglSales:$('#Add-Neraca-TglSales').val(),
				vendorId:$('#Add-Neraca-VendorId').val(),
				zeroControl:$('#Add-Neraca-ZeroControl').val(),

            }
            RequestData('POST', `/v1/Neraca/add`, '#md-Neraca-add', null, JSON.stringify(param), function (data_obj) {
                if (data_obj.succeeded) {
                    ShowNotif("Data Berhasil Disimpan", "success");
                    $('#md-Neraca-add').modal('hide');
                    getListNeraca();
                }
                else if (data_obj.code == 401) { //unathorized
                    AlertMessage(data_obj.message);
                } else
                    ShowNotif(`${data_obj.message} : ${data_obj.description}`, "error");
            });
        }
    });
}
