$(document).ready(function () {
    $('#Pajak-addBtn').on('click', function () {
        $('.clear').val('');
        $('#md-Pajak-add').modal('show');
        $('#Pajak-create_btn').unbind();
        $('#Pajak-create_btn').on('click', function () {
            addPajakSave();
        });
    });
});

function addPajakSave() {
    ConfirmMessage('Apakah Anda Yakin?', function (isConfirm) {
        if (isConfirm) {
            var param = {
				fileDokumen:$('#Add-Pajak-FileDokumen').val(),
				fileDokumenId:$('#Add-Pajak-FileDokumenId').val(),
				kondisi:$('#Add-Pajak-Kondisi').val(),
				noDokumen:$('#Add-Pajak-NoDokumen').val(),
				periodeAkhir:$('#Add-Pajak-PeriodeAkhir').val(),
				periodeAwal:$('#Add-Pajak-PeriodeAwal').val(),
				tahun:$('#Add-Pajak-Tahun').val(),
				tanggal:$('#Add-Pajak-Tanggal').val(),
				tanggalAkhir:$('#Add-Pajak-TanggalAkhir').val(),
				tipeDokumen:$('#Add-Pajak-TipeDokumen').val(),
				vendorId:$('#Add-Pajak-VendorId').val(),

            }
            RequestData('POST', `/v1/Pajak/add`, '#md-Pajak-add', null, JSON.stringify(param), function (data_obj) {
                if (data_obj.succeeded) {
                    ShowNotif("Data Berhasil Disimpan", "success");
                    $('#md-Pajak-add').modal('hide');
                    getListPajak();
                }
                else if (data_obj.code == 401) { //unathorized
                    AlertMessage(data_obj.message);
                } else
                    ShowNotif(`${data_obj.message} : ${data_obj.description}`, "error");
            });
        }
    });
}
