function editPajakDialog(el) {
    var data = $(el).data('detail');
    $('.clear').val('');
    $('#md-Pajak-edit').modal('show');

	$('#Edit-Pajak-FileDokumen').val(data.fileDokumen);
	$('#Edit-Pajak-FileDokumenId').val(data.fileDokumenId);
	$('#Edit-Pajak-Kondisi').val(data.kondisi);
	$('#Edit-Pajak-NoDokumen').val(data.noDokumen);
	$('#Edit-Pajak-PeriodeAkhir').val(data.periodeAkhir);
	$('#Edit-Pajak-PeriodeAwal').val(data.periodeAwal);
	$('#Edit-Pajak-Tahun').val(data.tahun);
	$('#Edit-Pajak-Tanggal').val(data.tanggal);
	$('#Edit-Pajak-TanggalAkhir').val(data.tanggalAkhir);
	$('#Edit-Pajak-TipeDokumen').val(data.tipeDokumen);
	$('#Edit-Pajak-VendorId').val(data.vendorId);


    $('#md-Pajak-edit').data('id', data.id);
    $('#Pajak-edit_btn').unbind();
    $('#Pajak-edit_btn').on('click', function () {
        editPajakSave();
    });
}

function editPajakSave() {
    ConfirmMessage('Apakah Anda Yakin Akan Mengubah Data Ini?', isConfirm => {
        if (isConfirm) {
            var param = {
				fileDokumen:$('#Edit-Pajak-FileDokumen').val(),
				fileDokumenId:$('#Edit-Pajak-FileDokumenId').val(),
				kondisi:$('#Edit-Pajak-Kondisi').val(),
				noDokumen:$('#Edit-Pajak-NoDokumen').val(),
				periodeAkhir:$('#Edit-Pajak-PeriodeAkhir').val(),
				periodeAwal:$('#Edit-Pajak-PeriodeAwal').val(),
				tahun:$('#Edit-Pajak-Tahun').val(),
				tanggal:$('#Edit-Pajak-Tanggal').val(),
				tanggalAkhir:$('#Edit-Pajak-TanggalAkhir').val(),
				tipeDokumen:$('#Edit-Pajak-TipeDokumen').val(),
				vendorId:$('#Edit-Pajak-VendorId').val(),

            }
            RequestData('PUT', `/v1/Pajak/edit/${$('#md-Pajak-edit').data('id')}`, '#md-Pajak-edit .modal-content', null, JSON.stringify(param), function (data_obj) {
                if (data_obj.succeeded) {
                    ShowNotif("Data Berhasil Dirubah", "success");
                    $('#md-Pajak-edit').modal('hide');
                    getListPajak();
                }
                else if (data_obj.code == 401) { //unathorized
                    AlertMessage(data_obj.message);
                } else
                    ShowNotif(`${data_obj.message} : ${data_obj.description}`, "error");
            });
        }
    });
}
